sysauto_script.script:
var positions = gs.getProperty('position_level');
var gt = new GlideDateTime();
var currentMonth = gt.getMonth();
var quarter = false;
if (currentMonth == 1 || currentMonth == 4 || currentMonth == 7 || currentMonth == 10) {
    quarter = true;
}
if (quarter) {
    var grEmployee = new GlideRecord('x_978579_corpora_0_employee');
    grEmployee.addQuery('position_level', 'IN', positions);
    grEmployee.query();
    while (grEmployee.next()) {
        grEmployee.setValue('e_wallet_balance', 8000);
        grEmployee.update();
    }
    gs.debug('e wallett budget reset is successfull');
} else {
    gs.debug('reset is not succssfull');
}
